<span class="line"><span style="color:#C792EA;">var</span><span style="color:#BABED8;"> str2 </span><span style="color:#89DDFF;">=</span><span style="color:#BABED8;"> arr6</span><span style="color:#89DDFF;">.</span><span style="color:#82AAFF;">join</span><span style="color:#BABED8;">(</span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">*</span><span style="color:#89DDFF;">&#39;</span><span style="color:#BABED8;">)</span></span>
